import React, { useState, useEffect } from 'react';
import styled from "styled-components";
import bg from './img/bg.png';
import { MainLayout } from './styles/Layouts';
import Orb from './Components/Orb/Orb';
import Navigation from './Components/Navigation/Navigation';
import Dashboard from './Components/Dashboard/Dashboard';
import Income from './Components/Income/Income';
import Expenses from './Components/Expenses/Expenses';
import { useGlobalContext } from './context/globalContext';
import Signin from './Components/auth/Signin';
import { Routes, Route } from 'react-router-dom'; // Import for React Router

function App() {
  const [active, setActive] = useState(1);
  const global = useGlobalContext();
  const [isSignedIn, setIsSignedIn] = useState(false); // State for sign-in

  useEffect(() => {
    // Check Clerk's sign-in state on component mount
    const isUserSignedIn = Clerk.userExists();
    setIsSignedIn(isUserSignedIn);
  }, []);

  return (
    <AppStyled bg={bg} className="App">
      {orbMemo}
      <MainLayout>
        <Navigation active={active} setActive={setActive} />
        <main>
          <Routes>
            <Route path="/" element={<Dashboard />} />
            <Route path="/income" element={<Income />} />
            <Route path="/expenses" element={<Expenses />} />
            <Route path="/sign-in" element={<Signin />} />
          </Routes>
          {!isSignedIn && <Signin />} {/* Render Signin if not signed in */}
        </main>
      </MainLayout>
    </AppStyled>
  );
}

const AppStyled = styled.div`
  height: 100vh;
  background-image: url(${props => props.bg});
  position: relative;
  main {
    flex: 1;
    background: rgba(252, 246, 249, 0.78);
    border: 3px solid #FFFFFF;
    backdrop-filter: blur(4.5px);
    border-radius: 32px;
    overflow-x: hidden;
    &::-webkit-scrollbar {
      width: 0;
    }
  }
`;

export default App;
